import React, { useState } from 'react';
import { X, ThumbsUp, ThumbsDown } from 'lucide-react';

const BlogModal = ({ isZooming, closeBlogModal, blogPosts, userVotes, handleVote, setIsCreatePostModalOpen }) => {
  const [filter, setFilter] = useState('all');

  const filteredPosts = blogPosts.filter(post => {
    if (filter === 'hot') return post.hotVotes > post.coldVotes;
    if (filter === 'cold') return post.coldVotes > post.hotVotes;
    if (filter === 'neutral') return post.hotVotes === post.coldVotes;
    return true; // 'all' filter
  });

  return (
    <div className={`fixed inset-0 bg-black bg-opacity-50 flex items-center justify-center z-50 ${isZooming ? 'scale-100' : 'scale-90'} transition-transform duration-300`}>
      <div className="bg-white rounded-lg w-full max-w-2xl h-[80vh] flex flex-col">
        <div className="flex justify-between items-center p-4 border-b">
          <h2 className="text-xl font-bold">Blog Posts</h2>
          <button onClick={closeBlogModal} className="text-gray-500 hover:text-gray-700">
            <X size={24} />
          </button>
        </div>
        <div className="flex space-x-2 p-4 border-b">
          <button onClick={() => setFilter('all')} className={`px-3 py-1 rounded ${filter === 'all' ? 'bg-blue-500 text-white' : 'bg-gray-200'}`}>All</button>
          <button onClick={() => setFilter('hot')} className={`px-3 py-1 rounded ${filter === 'hot' ? 'bg-red-500 text-white' : 'bg-gray-200'}`}>Hot Takes üî•</button>
          <button onClick={() => setFilter('cold')} className={`px-3 py-1 rounded ${filter === 'cold' ? 'bg-blue-500 text-white' : 'bg-gray-200'}`}>Cold Takes ‚ùÑÔ∏è</button>
          <button onClick={() => setFilter('neutral')} className={`px-3 py-1 rounded ${filter === 'neutral' ? 'bg-green-500 text-white' : 'bg-gray-200'}`}>Neutral Takes üü¢</button>
        </div>
        <div className="flex-grow overflow-y-auto p-4">
          {filteredPosts.map(post => (
            <div key={post.id} className="bg-gray-100 rounded-lg p-4 mb-4">
              <h3 className="text-lg font-semibold mb-2">{post.postName}</h3>
              <p className="text-gray-700 mb-2">{post.postContent}</p>
              <div className="flex items-center space-x-4">
                <button 
                  onClick={() => handleVote(post.id, 'hot')} 
                  className={`flex items-center space-x-1 ${userVotes[post.id] === 'hot' ? 'text-red-500' : 'text-gray-500'}`}
                >
                  <ThumbsUp size={20} />
                  <span>{post.hotVotes || 0}</span>
                </button>
                <button 
                  onClick={() => handleVote(post.id, 'cold')} 
                  className={`flex items-center space-x-1 ${userVotes[post.id] === 'cold' ? 'text-blue-500' : 'text-gray-500'}`}
                >
                  <ThumbsDown size={20} />
                  <span>{post.coldVotes || 0}</span>
                </button>
              </div>
            </div>
          ))}
        </div>
        <div className="p-4 border-t">
          <button 
            onClick={() => setIsCreatePostModalOpen(true)} 
            className="w-full bg-blue-500 text-white rounded-lg py-2 hover:bg-blue-600 transition-colors"
          >
            Create New Post
          </button>
        </div>
      </div>
    </div>
  );
};

export default BlogModal;
