import React, { useState, useEffect, useRef } from 'react';
import { Grid, User, FileText, ChevronLeft, Search, Linkedin, Github, Mail, ThumbsUp, ThumbsDown } from 'lucide-react';

const InstagramProfile = () => {
  const [activeTab, setActiveTab] = useState('projects');
  const [isTransitioning, setIsTransitioning] = useState(false);
  const [highlightStyle, setHighlightStyle] = useState({});
  const [isBlogModalOpen, setIsBlogModalOpen] = useState(false);
  const [isZooming, setIsZooming] = useState(false);
  const [activeBlogCategory, setActiveBlogCategory] = useState('all');
  const [searchTerm, setSearchTerm] = useState('');
  const [blogPosts, setBlogPosts] = useState([
    { id: 1, title: 'Why PC Gaming is Superior', date: '1w ago', content: 'PC gaming offers unparalleled flexibility and power.', hotVotes: 5, coldVotes: 2 },
    { id: 2, title: 'The History of Video Games', date: '3w ago', content: 'Video games have evolved significantly since the 1970s.', hotVotes: 2, coldVotes: 8 },
    { id: 3, title: 'My Controversial Gaming Opinions', date: '1m ago', content: 'Sometimes, linear storytelling is better than open-world.', hotVotes: 10, coldVotes: 10 },
    { id: 4, title: 'Evolution of Game Engines', date: '2m ago', content: 'Game engines have become more accessible to indie developers.', hotVotes: 3, coldVotes: 7 },
    { id: 5, title: 'Why Mobile Gaming is Underrated', date: '3m ago', content: 'Mobile games have come a long way in terms of quality.', hotVotes: 8, coldVotes: 4 },
  ]);
  
  const tabRefs = useRef({});
  const blogModalRef = useRef(null);

  const profileData = {
    username: 'Jeremiah Warinner',
    quote: '"The only legitimate use of a computer is to play games." - Eugene Jarvis',
    linkedin: 'https://www.linkedin.com/in/yourprofile',
    github: 'https://github.com/yourusername',
    email: 'mailto:your.email@example.com',
  };

  const projects = [
    { title: 'Project 1', image: '/api/placeholder/300/300' },
    { title: 'Project 2', image: '/api/placeholder/300/300' },
    { title: 'Project 3', image: '/api/placeholder/300/300' },
  ];

  const aboutMe = [
    { title: 'Skills', content: 'React, JavaScript, CSS' },
    { title: 'Education', content: 'Computer Science Degree' },
    { title: 'Hobbies', content: 'Gaming, Coding, Reading' },
  ];

  const totalPosts = blogPosts.length;
  const coldTakes = blogPosts.filter(post => post.coldVotes > post.hotVotes).length;
  const hotTakes = blogPosts.filter(post => post.hotVotes > post.coldVotes).length;
  const neutralTakes = blogPosts.filter(post => post.hotVotes === post.coldVotes).length;

  useEffect(() => {
    if (isTransitioning) {
      const timer = setTimeout(() => setIsTransitioning(false), 300);
      return () => clearTimeout(timer);
    }
  }, [isTransitioning]);

  useEffect(() => {
    const updateHighlight = () => {
      const currentTab = tabRefs.current[activeTab];
      if (currentTab) {
        const { offsetLeft, offsetWidth } = currentTab;
        setHighlightStyle({
          left: `${offsetLeft}px`,
          width: `${offsetWidth}px`,
        });
      }
    };

    updateHighlight();
    window.addEventListener('resize', updateHighlight);
    return () => window.removeEventListener('resize', updateHighlight);
  }, [activeTab]);

  const handleTabChange = (tab) => {
    setIsTransitioning(true);
    setActiveTab(tab);
    if (tab === 'blog') {
      openBlogModal();
    } else {
      setTimeout(() => setIsTransitioning(false), 150);
    }
  };

  const openBlogModal = () => {
    setIsBlogModalOpen(true);
    setTimeout(() => {
      setIsZooming(true);
    }, 50);
  };

  const closeBlogModal = () => {
    setIsZooming(false);
    setTimeout(() => {
      setIsBlogModalOpen(false);
      setActiveTab('projects');
      setIsTransitioning(false);
    }, 300);
  };

  const handleSearch = (event) => {
    setSearchTerm(event.target.value);
    setActiveBlogCategory('all');
  };

  const handleVote = (postId, voteType) => {
    setBlogPosts(prevPosts => 
      prevPosts.map(post => 
        post.id === postId
          ? { ...post, [voteType]: post[voteType] + 1 }
          : post
      )
    );
  };

  const getPostCategory = (post) => {
    if (post.hotVotes > post.coldVotes) return 'hot';
    if (post.coldVotes > post.hotVotes) return 'cold';
    return 'neutral';
  };

  const filteredBlogPosts = blogPosts.filter(post => 
    (activeBlogCategory === 'all' || getPostCategory(post) === activeBlogCategory) &&
    (post.title.toLowerCase().includes(searchTerm.toLowerCase()) ||
     post.content.toLowerCase().includes(searchTerm.toLowerCase()))
  );

  const renderGrid = () => {
    switch (activeTab) {
      case 'projects':
        return projects.map((project, index) => (
          <div key={index} className="aspect-square bg-gray-200 flex items-center justify-center">
            <img src={project.image} alt={project.title} className="w-full h-full object-cover" />
          </div>
        ));
      case 'about':
        return aboutMe.map((item, index) => (
          <div key={index} className="aspect-square bg-white border border-gray-200 p-4 flex flex-col justify-center">
            <h3 className="font-bold text-lg mb-2">{item.title}</h3>
            <p>{item.content}</p>
          </div>
        ));
      default:
        return null;
    }
  };

  const renderBlogModal = () => (
    <div 
      ref={blogModalRef}
      className={`fixed top-0 left-0 w-full h-full bg-white z-50 overflow-auto transition-all duration-500 ease-in-out ${
        isZooming ? 'scale-100 opacity-100' : 'scale-95 opacity-0'
      }`}
    >
      <div className="flex items-center justify-between p-4 border-b border-gray-200">
        <div className="flex items-center">
          <button onClick={closeBlogModal} className="mr-2">
            <ChevronLeft />
          </button>
          <h2 className="font-semibold">Blog Posts</h2>
        </div>
        <Search className="text-gray-500" />
      </div>
      <div className="p-4">
        <div className="bg-gray-100 rounded-full p-2 mb-4 flex items-center">
          <Search className="text-gray-500 mr-2" size={16} />
          <input
            type="text"
            placeholder="Search posts"
            className="bg-transparent w-full outline-none text-sm"
            value={searchTerm}
            onChange={handleSearch}
          />
        </div>
        <div className="flex mb-4">
          {['hot', 'cold', 'neutral', 'all'].map((category) => (
            <button 
              key={category}
              className={`mr-4 font-semibold transition-colors duration-300 ${
                activeBlogCategory === category 
                  ? category === 'hot' 
                    ? 'text-red-500' 
                    : category === 'cold' 
                      ? 'text-blue-500' 
                      : category === 'neutral'
                        ? 'text-green-500'
                        : 'text-black'
                  : 'text-gray-500'
              }`}
              onClick={() => setActiveBlogCategory(category)}
            >
              {category === 'hot' ? 'Hot Takes 游댠' : category === 'cold' ? 'Cold Takes 仇勇' : category === 'neutral' ? 'Neutral Takes 游릭' : 'All'}
            </button>
          ))}
        </div>
        <div className="space-y-4 transition-all duration-300">
          {filteredBlogPosts.map((post) => (
            <div key={post.id} className="flex items-center p-3 bg-gray-50 rounded-lg transition-all duration-300 hover:bg-gray-100">
              <div className="w-12 h-12 bg-gray-300 rounded-full mr-3 flex-shrink-0"></div>
              <div className="flex-grow">
                <h3 className="font-semibold">{post.title}</h3>
                <p className="text-gray-500 text-sm">{post.date}</p>
              </div>
              <div className="flex items-center">
                <button onClick={() => handleVote(post.id, 'hotVotes')} className="mr-2 p-1 rounded hover:bg-red-100">
                  <ThumbsUp size={16} className="text-red-500" />
                </button>
                <span className="mr-2">{post.hotVotes}</span>
                <button onClick={() => handleVote(post.id, 'coldVotes')} className="mr-2 p-1 rounded hover:bg-blue-100">
                  <ThumbsDown size={16} className="text-blue-500" />
                </button>
                <span className="mr-2">{post.coldVotes}</span>
                <div className="text-xl">
                  {getPostCategory(post) === 'hot' ? '游댠' : getPostCategory(post) === 'cold' ? '仇勇' : '游릭'}
                </div>
              </div>
            </div>
          ))}
        </div>
      </div>
    </div>
  );

  const tabs = [
    { name: 'PROJECTS', icon: Grid, value: 'projects' },
    { name: 'ABOUT ME', icon: User, value: 'about' },
    { name: 'BLOG', icon: FileText, value: 'blog' },
  ];

  return (
    <div className="max-w-3xl mx-auto p-4">
      <header className="flex items-center mb-8">
        <div className="w-20 h-20 rounded-full bg-gray-300 mr-8"></div>
        <div>
          <h1 className="text-2xl font-bold mb-2">{profileData.username}</h1>
          <div className="flex space-x-4 mb-2">
            <span><strong>{totalPosts}</strong> posts</span>
            <span><strong>{coldTakes}</strong> 仇勇</span>
            <span><strong>{hotTakes}</strong> 游댠</span>
            <span><strong>{neutralTakes}</strong> 游릭</span>
          </div>
          <div className="font-medium text-sm italic mb-2">{profileData.quote}</div>
          <div className="flex space-x-4">
            <a href={profileData.linkedin} target="_blank" rel="noopener noreferrer" className="text-blue-500 hover:text-blue-700">
              <Linkedin size={20} />
            </a>
            <a href={profileData.github} target="_blank" rel="noopener noreferrer" className="text-gray-700 hover:text-black">
              <Github size={20} />
            </a>
            <a href={profileData.email} className="text-red-500 hover:text-red-700">
              <Mail size={20} />
            </a>
          </div>
        </div>
      </header>

      <nav className="border-t border-gray-300 mb-8 relative">
        <div 
          className="absolute top-0 h-0.5 bg-black transition-all duration-300 ease-in-out"
          style={highlightStyle}
        />
        <ul className="flex justify-center space-x-12 -mt-px">
          {tabs.map((tab) => (
            <li 
              key={tab.value} 
              ref={el => tabRefs.current[tab.value] = el}
              className="pt-3"
            >
              <button 
                onClick={() => handleTabChange(tab.value)}
                className={`flex items-center transition-colors duration-300 ${activeTab === tab.value ? 'text-black' : 'text-gray-500'}`}
              >
                <tab.icon size={12} className="mr-1" />
                {tab.name}
              </button>
            </li>
          ))}
        </ul>
      </nav>

      <div className={`grid grid-cols-3 gap-4 transition-opacity duration-300 ${isTransitioning ? 'opacity-0' : 'opacity-100'}`}>
        {renderGrid()}
      </div>

      {isBlogModalOpen && renderBlogModal()}
    </div>
  );
};

export default InstagramProfile;
