import React from 'react';
import { X, ThumbsUp, ThumbsDown } from 'lucide-react';
import ReactMarkdown from 'react-markdown';

const PostModal = ({ post, onClose, handleVote, userVotes }) => {
  return (
    <div className="fixed inset-0 bg-white z-60 flex flex-col">
      <div className="flex justify-between items-center p-4 border-b border-gray-200">
        <h2 className="font-semibold text-xl">{post.postName}</h2>
        <button onClick={onClose} className="text-gray-500 hover:text-gray-700">
          <X size={24} />
        </button>
      </div>
      <div className="flex-grow overflow-auto p-4">
        <div className="max-w-3xl mx-auto">
          <ReactMarkdown className="prose prose-sm sm:prose lg:prose-lg xl:prose-xl mx-auto">
            {post.postContent}
          </ReactMarkdown>
        </div>
      </div>
      <div className="border-t border-gray-200 p-4">
        <div className="flex items-center justify-center max-w-3xl mx-auto">
          <button 
            onClick={() => handleVote(post.id, 'hot')}
            className={`mr-2 p-2 rounded ${userVotes[post.id] === 'hot' ? 'bg-red-100' : 'hover:bg-red-100'}`}
          >
            <ThumbsUp size={24} className={`${userVotes[post.id] === 'hot' ? 'text-red-500' : 'text-gray-500'}`} />
          </button>
          <span className="mr-4 text-lg">{post.hotVotes || 0}</span>
          <button 
            onClick={() => handleVote(post.id, 'cold')}
            className={`mr-2 p-2 rounded ${userVotes[post.id] === 'cold' ? 'bg-blue-100' : 'hover:bg-blue-100'}`}
          >
            <ThumbsDown size={24} className={`${userVotes[post.id] === 'cold' ? 'text-blue-500' : 'text-gray-500'}`} />
          </button>
          <span className="text-lg">{post.coldVotes || 0}</span>
        </div>
      </div>
    </div>
  );
};

export default PostModal;
