import React, { useEffect, useState } from 'react';
import { useParams, useNavigate } from 'react-router-dom';
import { ChevronLeft } from 'lucide-react';
import ReactMarkdown from 'react-markdown';
import CustomCursor from './CustomCursor';

const PostPage = () => {
  const [post, setPost] = useState(null);
  const { id } = useParams();
  const navigate = useNavigate();

  useEffect(() => {
    const fetchPost = async () => {
      try {
        const response = await fetch(`http://127.0.0.1:5000/get_post/${id}`);
        if (!response.ok) {
          throw new Error('Failed to fetch post');
        }
        const data = await response.json();
        setPost(data);
      } catch (error) {
        console.error('Error fetching post:', error);
        // Handle error (e.g., show error message or redirect)
      }
    };
    fetchPost();
  }, [id]);

  const handleBackClick = () => {
    navigate('/', { state: { openBlogModal: true } });
  };

  if (!post) {
    return <div className="text-purple-300">Loading...</div>;
  }

  return (
    <div className="fixed inset-0 bg-gray-900 z-50 overflow-auto">
        <CustomCursor/>
      <div className="max-w-3xl mx-auto p-4">
        <button onClick={handleBackClick} className="mb-4 text-purple-400 hover:text-purple-300 flex items-center">
          <ChevronLeft size={20} />
          <span>Back to Blog</span>
        </button>
        <h1 className="text-3xl font-bold mb-4 text-purple-300">{post.postName}</h1>
        <ReactMarkdown className="prose prose-lg prose-invert prose-purple">
          {post.postContent}
        </ReactMarkdown>
      </div>
    </div>
  );
};

export default PostPage;
