import React, { useState, useEffect, useRef } from 'react';
import { Grid, User, FileText } from 'lucide-react';
import { profileData, projects, aboutMe } from './data';
import ProjectGrid from './ProjectGrid';
import AboutGrid from './AboutGrid';
import ProfileHeader from './ProfileHeader';
import TabNavigation from './TabNavigation';

const InstagramProfile = () => {
  console.log('InstagramProfile component rendering');

  const [activeTab, setActiveTab] = useState('projects');
  const [isTransitioning, setIsTransitioning] = useState(false);
  const [blogPosts, setBlogPosts] = useState([]);
  const tabRefs = useRef({});

  useEffect(() => {
    console.log('InstagramProfile useEffect - fetching blog posts');
    fetchBlogPosts();
  }, []);

  useEffect(() => {
    console.log('InstagramProfile useEffect - handling transition, isTransitioning:', isTransitioning);
    if (isTransitioning) {
      const timer = setTimeout(() => {
        console.log('Transition timeout completed, setting isTransitioning to false');
        setIsTransitioning(false);
      }, 300);
      return () => clearTimeout(timer);
    }
  }, [isTransitioning]);

  const fetchBlogPosts = async () => {
    console.log('Fetching blog posts from backend');
    try {
      const response = await fetch('http://127.0.0.1:5000/get_posts');
      if (!response.ok) {
        throw new Error(`HTTP error! status: ${response.status}`);
      }
      const data = await response.json();
      console.log('Fetched blog posts:', data);
      setBlogPosts(data.posts);
    } catch (error) {
      console.error('Error fetching blog posts:', error);
    }
  };

  const handleTabChange = (tabValue) => {
    console.log('Tab change triggered, new tab:', tabValue);
    setIsTransitioning(true);
    setActiveTab(tabValue);
  };

  const tabs = [
    { name: 'PROJECTS', icon: Grid, value: 'projects' },
    { name: 'ABOUT ME', icon: User, value: 'about' },
    { name: 'BLOG', icon: FileText, value: 'blog' },
  ];

  const renderContent = () => {
    console.log('Rendering content for tab:', activeTab);
    try {
      switch(activeTab) {
        case 'projects':
          console.log('Rendering ProjectGrid');
          return <ProjectGrid projects={projects} />;
        case 'about':
          console.log('Rendering AboutGrid');
          return <AboutGrid aboutMe={aboutMe} />;
        case 'blog':
          console.log('Rendering blog posts');
          return (
            <div className="space-y-4">
              {blogPosts.map((post) => (
                <div key={post.id} className="bg-white p-4 rounded-lg shadow">
                  <h3 className="text-lg font-semibold">{post.postName}</h3>
                  <p className="mt-2">{post.postContent}</p>
                  <div className="mt-2 flex space-x-4">
                    <span>Hot: {post.hotLikeCount}</span>
                    <span>Cold: {post.coldLikeCount}</span>
                  </div>
                </div>
              ))}
            </div>
          );
        default:
          console.log('No matching tab found');
          return null;
      }
    } catch (error) {
      console.error('Error rendering content:', error);
      return <div>Error rendering content</div>;
    }
  };

  console.log('InstagramProfile render - Current activeTab:', activeTab);

  return (
    <div className="max-w-3xl mx-auto p-4">
      {console.log('Rendering ProfileHeader')}
      <ProfileHeader profileData={profileData} blogPosts={blogPosts} />
      
      {console.log('Rendering TabNavigation')}
      <TabNavigation 
        tabs={tabs}
        activeTab={activeTab}
        handleTabChange={handleTabChange}
        tabRefs={tabRefs}
      />

      {console.log('Rendering content container')}
      <div className={`grid grid-cols-1 gap-4 transition-opacity duration-300 ${isTransitioning ? 'opacity-0' : 'opacity-100'}`}>
        {renderContent()}
      </div>
    </div>
  );
};

export default InstagramProfile;
