import React, { useState, useEffect, useCallback, useRef } from 'react';
import { 
  Grid, 
  User, 
  FileText, 
  ChevronLeft, 
  Search, 
  Linkedin, 
  Github, 
  Mail, 
  BookOpen, 
  Briefcase, 
  GraduationCap, 
  ExternalLink,
  ThumbsUp,
  ThumbsDown,
  PlusCircle
} from 'lucide-react';
import CreateBlogPost from './CreateBlogPost';

const InstagramProfile = () => {
  const [activeTab, setActiveTab] = useState('projects');
  const [isTransitioning, setIsTransitioning] = useState(false);
  const [highlightStyle, setHighlightStyle] = useState({});
  const [isBlogModalOpen, setIsBlogModalOpen] = useState(false);
  const [isAboutModalOpen, setIsAboutModalOpen] = useState(false);
  const [activeAboutSection, setActiveAboutSection] = useState(null);
  const [isProjectModalOpen, setIsProjectModalOpen] = useState(false);
  const [activeProject, setActiveProject] = useState(null);
  const [isZooming, setIsZooming] = useState(false);
  const [activeBlogCategory, setActiveBlogCategory] = useState('all');
  const [searchTerm, setSearchTerm] = useState('');
  const [blogPosts, setBlogPosts] = useState([]);
  const [isCreatePostModalOpen, setIsCreatePostModalOpen] = useState(false);
  const [userVotes, setUserVotes] = useState({});
  
  const tabRefs = useRef({});
  const blogModalRef = useRef(null);

  const profileData = {
    username: 'Jeremiah Warinner',
    quote: '"The only legitimate use of a computer is to play games." - Eugene Jarvis',
    linkedin: 'https://www.linkedin.com/in/jeremiah-warinner-8b162821a/',
    github: 'https://github.com/jeremiahwarinner',
    email: 'mailto:warinnerjeremiah@gmail.com',
  };

  const projects = [
    {
      title: "Inbox Guardian",
      description: "An SMS spam filter for Android devices using AI and machine learning.",
      image: "https://github.com/Lopezm0908/SMS-filtering/assets/158241209/37b663ed-8a21-495c-a308-d81725945f6b",
      color: "bg-blue-100 text-blue-800",
      details: `Inbox Guardian is an SMS spam filter meant to be deployed in addition to any existing SMS application. It uses a logistic regression model, the BERT language model, and other holistic methods such as keyword recognition to flag spam messages with a tested 90% accuracy rate.`,
      link: "https://github.com/Lopezm0908/SMS-filtering"
    },
    {
      title: "Schedule Management Application",
      description: "An application to manage employee schedules with various features.",
      image: "https://github.com/jeremiahwarinner/Schedule-Manager/assets/158241209/50fe9b23-c28a-42e4-afb7-3fa8efe99867",
      color: "bg-green-100 text-green-800",
      details: `This application is designed to manage employee schedules, including adding and updating employee information, assigning shifts, and exporting schedules to Excel.`,
      link: "https://github.com/jeremiahwarinner/Schedule-Manager"
    },
    {
      title: "Major Employment and Salary Analysis Dashboard",
      description: "An interactive dashboard to analyze employment and salary data of different majors.",
      image: "https://github.com/jeremiahwarinner/Major-Employment-and-Salary-Analysis-Dashboard/assets/158241209/896e651a-52bb-455e-bf6e-f354ed6acb5e",
      color: "bg-purple-100 text-purple-800",
      details: `This project provides an interactive dashboard to analyze the employment and salary data of different majors. It is built using Python, Dash, and Plotly.`,
      link: "https://github.com/jeremiahwarinner/Major-Employment-and-Salary-Analysis-Dashboard"
    },
    {
      title: "Salary Trends Visualizer",
      description: "A data analysis project to explore and visualize salary trends across various dimensions.",
      image: "https://github.com/jeremiahwarinner/Salary-Trends-Visualizer/assets/158241209/85a19575-670d-4329-9e44-7d851b85f755",
      color: "bg-red-100 text-red-800",
      details: `Salary Trends Visualizer is designed to explore and uncover trends in salary data. It analyzes a dataset of salaries to provide insights into distributions across job titles, experience levels, and remote work arrangements.`,
      link: "https://github.com/jeremiahwarinner/Salary-Trends-Visualizer"
    }
  ];

  const aboutMe = [
    { 
      title: 'My Story', 
      icon: BookOpen,
      color: 'bg-purple-100 text-purple-800',
      content: 'As a passionate Computer Science student with a projected graduation in August 2024, I\'m driven by a curiosity for technology and its potential to solve real-world problems. My journey in tech has been marked by a series of challenging projects and valuable experiences, each contributing to my growth as a developer and problem-solver.'
    },
    { 
      title: 'My Experience', 
      icon: Briefcase,
      color: 'bg-blue-100 text-blue-800',
      content: `Full Stack Developer Intern at Luday, Gothenburg SE (July 2024 - Present):
      • Designed front end components in Figma
      • Built front-end components in Next.js
      • Built REST APIs using Python and Flask
      • Worked in an Agile Scrum environment

      Inventory Manager at Chick-fil-a, Pearland TX (November 2022 - May 2024):
      • Maintained and utilized Excel sheets to monitor inventory levels
      • Monitored sales trends to maintain optimal on-hand product levels
      • Performed repairs on equipment including Henny Penny fryers
      • Created and executed plans to improve sales, speed, and accuracy of operations

      Hiring Manager at Rolling Dough Ltd, Houston TX (January 2019 - November 2024):
      • Maintained and utilized Excel sheets to track employee hours and sales metrics
      • Automated several tasks such as schedule drafting using Python scripts
      • Performed repairs and maintenance on office and store equipment
      • Conducted interviews and performed resume analysis for candidate evaluation`
    },
    { 
      title: 'My Education', 
      icon: GraduationCap,
      color: 'bg-green-100 text-green-800',
      content: `Bachelor of Science in Computer Science
      University of Houston-Clear Lake, Houston TX
      Expected Graduation: August 2024

      Related Coursework: Software Engineering, Digital Circuits, Computer Architecture, Intro to Telecom and Networks, Assembly Language x86, Advanced Data Structures and Algorithms, Operating Systems, Design of Database Systems, Probability and Statistics, Computer Forensics, Web App Development, Computer Security, Computer Systems Admin, Network Protocols

      Technical Skills:
      • Programming Languages: Python, Java, Kotlin, SQL, HTML, CSS, JavaScript
      • Packages: Django, Pandas, Dash, Numpy, React, Next.js
      • Operating Systems: Linux, Windows
      • Tools: Github, Tableau, Excel, MySQL`
    }
  ];

  useEffect(() => {
    fetchBlogPosts();
  }, []);

  useEffect(() => {
    if (isTransitioning) {
      const timer = setTimeout(() => setIsTransitioning(false), 300);
      return () => clearTimeout(timer);
    }
  }, [isTransitioning]);

  useEffect(() => {
    const updateHighlight = () => {
      const currentTab = tabRefs.current[activeTab];
      if (currentTab) {
        const { offsetLeft, offsetWidth } = currentTab;
        setHighlightStyle({
          left: `${offsetLeft}px`,
          width: `${offsetWidth}px`,
        });
      }
    };

    updateHighlight();
    window.addEventListener('resize', updateHighlight);
    return () => window.removeEventListener('resize', updateHighlight);
  }, [activeTab]);

  const fetchBlogPosts = async () => {
    try {
      const response = await fetch('http://localhost:5000/get_posts');
      if (!response.ok) {
        throw new Error(`HTTP error! status: ${response.status}`);
      }
      const data = await response.json();
      setBlogPosts(data);
    } catch (error) {
      console.error('Error fetching blog posts:', error);
    }
  };

  const handleTabChange = (tab) => {
    setIsTransitioning(true);
    setActiveTab(tab);
    if (tab === 'blog') {
      openBlogModal();
    } else {
      setTimeout(() => setIsTransitioning(false), 150);
    }
  };

  const openBlogModal = () => {
    setIsBlogModalOpen(true);
    setTimeout(() => {
      setIsZooming(true);
    }, 50);
  };

  const closeBlogModal = () => {
    setIsZooming(false);
    setTimeout(() => {
      setIsBlogModalOpen(false);
      setActiveTab('projects');
      setIsTransitioning(false);
    }, 300);
  };

  const openAboutModal = (section) => {
    setActiveAboutSection(section);
    setIsAboutModalOpen(true);
    setTimeout(() => {
      setIsZooming(true);
    }, 50);
  };

  const closeAboutModal = () => {
    setIsZooming(false);
    setTimeout(() => {
      setIsAboutModalOpen(false);
      setActiveAboutSection(null);
    }, 300);
  };

  const openProjectModal = (project) => {
    setActiveProject(project);
    setIsProjectModalOpen(true);
    setTimeout(() => {
      setIsZooming(true);
    }, 50);
  };

  const closeProjectModal = () => {
    setIsZooming(false);
    setTimeout(() => {
      setIsProjectModalOpen(false);
      setActiveProject(null);
    }, 300);
  };

  const handleSearch = (event) => {
    setSearchTerm(event.target.value);
    setActiveBlogCategory('all');
  };

  const handleVote = (postId, voteType) => {
    setUserVotes(prevVotes => {
      const currentVote = prevVotes[postId];
      if (currentVote === voteType) {
        const { [postId]: _, ...rest } = prevVotes;
        return rest;
      } else {
        return { ...prevVotes, [postId]: voteType };
      }
    });

    setBlogPosts(prevPosts => 
      prevPosts.map(post => {
        if (post.id === postId) {
          const currentVote = userVotes[postId];
          let newHotVotes = post.hotVotes || 0;
          let newColdVotes = post.coldVotes || 0;

          if (currentVote === 'hot' && voteType === 'cold') {
            newHotVotes--;
            newColdVotes++;
          } else if (currentVote === 'cold' && voteType === 'hot') {
            newHotVotes++;
            newColdVotes--;
          } else if (!currentVote) {
            if (voteType === 'hot') newHotVotes++;
            if (voteType === 'cold') newColdVotes++;
          } else if (currentVote === voteType) {
            if (voteType === 'hot') newHotVotes--;
            if (voteType === 'cold') newColdVotes--;
          }

          return { ...post, hotVotes: newHotVotes, coldVotes: newColdVotes };
        }
        return post;
      })
    );
  };

  const getPostCategory = (post) => {
    const hotVotes = post.hotVotes || 0;
    const coldVotes = post.coldVotes || 0;
    if (hotVotes > coldVotes) return 'hot';
    if (coldVotes > hotVotes) return 'cold';
    return 'neutral';
  };

  const filteredBlogPosts = blogPosts.filter(post => 
    (activeBlogCategory === 'all' || getPostCategory(post) === activeBlogCategory) &&
    (post.title.toLowerCase().includes(searchTerm.toLowerCase()) ||
     post.content.toLowerCase().includes(searchTerm.toLowerCase()))
  );

  const handlePostCreated = (newPost) => {
    setBlogPosts(prevPosts => [newPost, ...prevPosts]);
    setIsCreatePostModalOpen(false);
  };

  const renderGrid = () => {
    switch (activeTab) {
      case 'projects':
        return projects.map((project, index) => (
          <div 
            key={index} 
            onClick={() => openProjectModal(project)} 
            className={`aspect-square ${project.color} p-6 rounded-xl shadow-lg flex flex-col items-center justify-center cursor-pointer hover:shadow-xl transition-all duration-300 transform hover:-translate-y-1`}
          >
            <img src={project.image} alt={project.title} className="w-20 h-20 object-cover rounded-full mb-4" />
            <h3 className="font-bold text-xl text-center font-serif">{project.title}</h3>
            <p className="text-sm text-center mt-2">{project.description}</p>
          </div>
        ));
      case 'about':
        return aboutMe.map((item, index) => (
          <div 
            key={index} 
            onClick={() => openAboutModal(item)} 
            className={`aspect-square ${item.color} p-6 rounded-xl shadow-lg flex flex-col items-center justify-center cursor-pointer hover:shadow-xl transition-all duration-300 transform hover:-translate-y-1`}
          >
            <item.icon size={48} className="mb-4" />
            <h3 className="font-bold text-xl text-center font-serif">{item.title}</h3>
          </div>
        ));
      default:
        return null;
    }
  };

  const renderBlogModal = () => (
    <div 
      ref={blogModalRef}
      className={`fixed top-0 left-0 w-full h-full bg-white z-50 overflow-auto transition-all duration-500 ease-in-out ${
        isZooming ? 'scale-100 opacity-100' : 'scale-95 opacity-0'
      }`}
    >
      <div className="flex items-center justify-between p-4 border-b border-gray-200">
        <div className="flex items-center">
          <button onClick={closeBlogModal} className="mr-2">
            <ChevronLeft />
          </button>
          <h2 className="font-semibold">Blog Posts</h2>
        </div>
        <div className="flex items-center">
          <button onClick={() => setIsCreatePostModalOpen(true)} className="mr-4 text-blue-500 hover:text-blue-700">
            <PlusCircle size={24} />
          </button>
          <Search className="text-gray-500" />
        </div>
      </div>
      <div className="p-4">
        <div className="bg-gray-100 rounded-full p-2 mb-4 flex items-center">
          <Search className="text-gray-500 mr-2" size={16} />
          <input
            type="text"
            placeholder="Search posts"
            className="bg-transparent w-full outline-none text-sm"
            value={searchTerm}
            onChange={handleSearch}
          />
        </div>
        <div className="flex mb-4">
          {['hot', 'cold', 'neutral', 'all'].map((category) => (
            <button 
              key={category}
              className={`mr-4 font-semibold transition-colors duration-300 ${
                activeBlogCategory === category 
                  ? category === 'hot' 
                    ? 'text-red-500' 
                    : category === 'cold' 
                      ? 'text-blue-500' 
                      : category === 'neutral'
                        ? 'text-green-500'
                        : 'text-black'
                  : 'text-gray-500'
              }`}
              onClick={() => setActiveBlogCategory(category)}
            >
              {category === 'hot' ? 'Hot Takes 🔥' : category === 'cold' ? 'Cold Takes ❄️' : category === 'neutral' ? 'Neutral Takes 🟢' : 'All'}
            </button>
          ))}
        </div>
        <div className="space-y-4 transition-all duration-300">
          {filteredBlogPosts.map((post) => (
            <div key={post.id} className="flex items-center p-3 bg-gray-50 rounded-lg transition-all duration-300 hover:bg-gray-100">
              <div className="w-12 h-12 bg-gray-300 rounded-full mr-3 flex-shrink-0"></div>
              <div className="flex-grow">
                <h3 className="font-semibold">{post.title}</h3>
                <p className="text-gray-500 text-sm">{post.date}</p>
                <p className="text-gray-700 mt-2">{post.content}</p>
              </div>
              <div className="flex items-center">
                <button 
                  onClick={() => handleVote(post.id, 'hot')} 
                  className={`mr-2 p-1 rounded ${
                    userVotes[post.id] === 'hot' 
                      ? 'bg-red-100' 
                      : 'hover:bg-red-100'
                  }`}
                >
                  <ThumbsUp size={16} className={`${userVotes[post.id] === 'hot' ? 'text-red-500' : 'text-gray-500'}`} />
                </button>
                <span className="mr-2">{post.hotVotes || 0}</span>
                <button 
                  onClick={() => handleVote(post.id, 'cold')} 
                  className={`mr-2 p-1 rounded ${
                    userVotes[post.id] === 'cold' 
                      ? 'bg-blue-100' 
                      : 'hover:bg-blue-100'
                  }`}
                >
                  <ThumbsDown size={16} className={`${userVotes[post.id] === 'cold' ? 'text-blue-500' : 'text-gray-500'}`} />
                </button>
                <span className="mr-2">{post.coldVotes || 0}</span>
                <div className="text-xl">
                  {getPostCategory(post) === 'hot' ? '🔥' : getPostCategory(post) === 'cold' ? '❄️' : '🟢'}
                </div>
              </div>
            </div>
          ))}
        </div>
      </div>
    </div>
  );

  const renderAboutModal = () => (
    <div 
      className={`fixed top-0 left-0 w-full h-full bg-white z-50 overflow-auto transition-all duration-500 ease-in-out ${
        isZooming ? 'scale-100 opacity-100' : 'scale-95 opacity-0'
      }`}
    >
      <div className={`flex items-center justify-between p-4 border-b border-gray-200 ${activeAboutSection?.color}`}>
        <div className="flex items-center">
          <button onClick={closeAboutModal} className="mr-2 hover:bg-white hover:bg-opacity-20 rounded-full p-1">
            <ChevronLeft size={24} />
          </button>
          <h2 className="font-bold text-3xl font-serif">{activeAboutSection?.title}</h2>
        </div>
      </div>
      <div className="max-w-4xl mx-auto p-8">
        <pre className="whitespace-pre-wrap font-sans text-lg leading-relaxed">{activeAboutSection?.content}</pre>
      </div>
    </div>
  );

  const renderProjectModal = () => (
    <div 
      className={`fixed top-0 left-0 w-full h-full bg-white z-50 overflow-auto transition-all duration-500 ease-in-out ${
        isZooming ? 'scale-100 opacity-100' : 'scale-95 opacity-0'
      }`}
    >
      <div className={`flex items-center justify-between p-4 border-b border-gray-200 ${activeProject?.color}`}>
        <div className="flex items-center">
          <button onClick={closeProjectModal} className="mr-2 hover:bg-white hover:bg-opacity-20 rounded-full p-1">
            <ChevronLeft size={24} />
          </button>
          <h2 className="font-bold text-3xl font-serif">{activeProject?.title}</h2>
        </div>
        <a href={activeProject?.link} target="_blank" rel="noopener noreferrer" className="flex items-center hover:underline">
          <ExternalLink size={20} className="mr-1" />
          View on GitHub
        </a>
      </div>
      <div className="max-w-4xl mx-auto p-8">
        <img src={activeProject?.image} alt={activeProject?.title} className="w-full h-64 object-cover rounded-xl mb-6" />
        <p className="text-xl leading-relaxed font-serif mb-6">{activeProject?.description}</p>
        <div className="space-y-4">
          <h3 className="text-2xl font-bold">Project Details</h3>
          <p className="text-lg whitespace-pre-wrap">{activeProject?.details}</p>
        </div>
      </div>
    </div>
  );

  const totalPosts = blogPosts.length;
  const coldTakes = blogPosts.filter(post => (post.coldVotes || 0) > (post.hotVotes || 0)).length;
  const hotTakes = blogPosts.filter(post => (post.hotVotes || 0) > (post.coldVotes || 0)).length;
  const neutralTakes = blogPosts.filter(post => (post.hotVotes || 0) === (post.coldVotes || 0)).length;

  const tabs = [
    { name: 'PROJECTS', icon: Grid, value: 'projects' },
    { name: 'ABOUT ME', icon: User, value: 'about' },
    { name: 'BLOG', icon: FileText, value: 'blog' },
  ];

  return (
    <div className="max-w-3xl mx-auto p-4">
      <header className="flex items-center mb-8">
        <div className="w-20 h-20 rounded-full bg-gray-300 mr-8"></div>
        <div>
          <h1 className="text-2xl font-bold mb-2">{profileData.username}</h1>
          <div className="flex space-x-4 mb-2">
            <span><strong>{totalPosts}</strong> posts</span>
            <span><strong>{coldTakes}</strong> ❄️</span>
            <span><strong>{hotTakes}</strong> 🔥</span>
            <span><strong>{neutralTakes}</strong> 🟢</span>
          </div>
          <div className="font-medium text-sm italic mb-2">{profileData.quote}</div>
          <div className="flex space-x-4">
            <a href={profileData.linkedin} target="_blank" rel="noopener noreferrer" className="text-blue-500 hover:text-blue-700">
              <Linkedin size={20} />
            </a>
            <a href={profileData.github} target="_blank" rel="noopener noreferrer" className="text-gray-700 hover:text-black">
              <Github size={20} />
            </a>
            <a href={profileData.email} className="text-red-500 hover:text-red-700">
              <Mail size={20} />
            </a>
          </div>
        </div>
      </header>

      <nav className="border-t border-gray-300 mb-8 relative">
        <div 
          className="absolute top-0 h-0.5 bg-black transition-all duration-300 ease-in-out"
          style={highlightStyle}
        />
        <ul className="flex justify-center space-x-12 -mt-px">
          {tabs.map((tab) => (
            <li 
              key={tab.value} 
              ref={el => tabRefs.current[tab.value] = el}
              className="pt-3"
            >
              <button 
                onClick={() => handleTabChange(tab.value)}
                className={`flex items-center transition-colors duration-300 ${activeTab === tab.value ? 'text-black' : 'text-gray-500'}`}
              >
                <tab.icon size={12} className="mr-1" />
                {tab.name}
              </button>
            </li>
          ))}
        </ul>
      </nav>

      <div className={`grid grid-cols-2 gap-4 transition-opacity duration-300 ${isTransitioning ? 'opacity-0' : 'opacity-100'}`}>
        {renderGrid()}
      </div>

      {isBlogModalOpen && renderBlogModal()}
      {isAboutModalOpen && renderAboutModal()}
      {isProjectModalOpen && renderProjectModal()}
      {isCreatePostModalOpen && (
        <CreateBlogPost
          onClose={() => setIsCreatePostModalOpen(false)}
          onPostCreated={handlePostCreated}
        />
      )}
    </div>
  );
};

export default InstagramProfile;
