import React, { useState, useRef } from 'react';
import { ChevronLeft, Search, PlusCircle } from 'lucide-react';
import PostModal from './PostModal';

const BlogModal = ({ isZooming, closeBlogModal, blogPosts, setIsCreatePostModalOpen }) => {
  const [searchTerm, setSearchTerm] = useState('');
  const [selectedPost, setSelectedPost] = useState(null);
  const blogModalRef = useRef(null);

  const handleSearch = (event) => {
    setSearchTerm(event.target.value);
  };

  const getFilteredBlogPosts = () => {
    return blogPosts.filter(post => {
      const postTitle = post.postName || post.title || ''; // Check for both postName and title
      return postTitle.toLowerCase().includes(searchTerm.toLowerCase());
    });
  };

  const filteredBlogPosts = getFilteredBlogPosts();

  const handlePostClick = (post) => {
    setSelectedPost(post);
  };

  return (
    <div 
      ref={blogModalRef}
      className={`fixed top-0 left-0 w-full h-full bg-white z-50 overflow-auto transition-all duration-500 ease-in-out ${
        isZooming ? 'scale-100 opacity-100' : 'scale-95 opacity-0'
      }`}
    >
      <div className="flex items-center justify-between p-4 border-b border-gray-200">
        <div className="flex items-center">
          <button onClick={closeBlogModal} className="mr-2">
            <ChevronLeft />
          </button>
          <h2 className="font-semibold">Blog Posts</h2>
        </div>
        <div className="flex items-center">
          <button onClick={() => setIsCreatePostModalOpen(true)} className="mr-4 text-blue-500 hover:text-blue-700">
            <PlusCircle size={24} />
          </button>
          <Search className="text-gray-500" />
        </div>
      </div>
      <div className="p-4">
        <div className="bg-gray-100 rounded-full p-2 mb-4 flex items-center">
          <Search className="text-gray-500 mr-2" size={16} />
          <input
            type="text"
            placeholder="Search posts"
            className="bg-transparent w-full outline-none text-sm"
            value={searchTerm}
            onChange={handleSearch}
          />
        </div>
        <div className="space-y-4 transition-all duration-300">
          {filteredBlogPosts.map((post) => (
            <div 
              key={post.id} 
              className="flex items-center p-3 bg-gray-50 rounded-lg transition-all duration-300 hover:bg-gray-100 cursor-pointer"
              onClick={() => handlePostClick(post)}
            >
              <div className="w-12 h-12 bg-gray-300 rounded-full mr-3 flex-shrink-0"></div>
              <div className="flex-grow">
                <h3 className="font-semibold">{post.postName || post.title}</h3>
                <p className="text-gray-500 text-sm">{post.date || 'No date'}</p>
              </div>
            </div>
          ))}
        </div>
      </div>
      {selectedPost && (
        <PostModal 
          post={selectedPost} 
          onClose={() => setSelectedPost(null)}
        />
      )}
    </div>
  );
};

export default BlogModal;
