import React, { useState, useRef } from 'react';
import { ChevronLeft, Search, PlusCircle, ThumbsUp, ThumbsDown } from 'lucide-react';
import PostModal from './PostModal'; // We'll create this component next

const BlogModal = ({ isZooming, closeBlogModal, blogPosts, userVotes, handleVote, setIsCreatePostModalOpen }) => {
  const [searchTerm, setSearchTerm] = useState('');
  const [activeBlogCategory, setActiveBlogCategory] = useState('all');
  const [selectedPost, setSelectedPost] = useState(null);
  const blogModalRef = useRef(null);

  const handleSearch = (event) => {
    setSearchTerm(event.target.value);
    setActiveBlogCategory('all');
  };

  const getPostCategory = (post) => {
    const hotVotes = post.hotVotes || 0;
    const coldVotes = post.coldVotes || 0;
    if (hotVotes > coldVotes) return 'hot';
    if (coldVotes > hotVotes) return 'cold';
    return 'neutral';
  };

  const getFilteredBlogPosts = () => {
    return blogPosts.filter(post => {
      const category = getPostCategory(post);
      const matchesCategory = activeBlogCategory === 'all' || category === activeBlogCategory;
      const matchesSearch = post.postName.toLowerCase().includes(searchTerm.toLowerCase());

      return matchesCategory && matchesSearch;
    });
  };

  const filteredBlogPosts = getFilteredBlogPosts();

  const handlePostClick = (post) => {
    setSelectedPost(post);
  };

  return (
    <div 
      ref={blogModalRef}
      className={`fixed top-0 left-0 w-full h-full bg-white z-50 overflow-auto transition-all duration-500 ease-in-out ${
        isZooming ? 'scale-100 opacity-100' : 'scale-95 opacity-0'
      }`}
    >
      <div className="flex items-center justify-between p-4 border-b border-gray-200">
        <div className="flex items-center">
          <button onClick={closeBlogModal} className="mr-2">
            <ChevronLeft />
          </button>
          <h2 className="font-semibold">Blog Posts</h2>
        </div>
        <div className="flex items-center">
          <button onClick={() => setIsCreatePostModalOpen(true)} className="mr-4 text-blue-500 hover:text-blue-700">
            <PlusCircle size={24} />
          </button>
          <Search className="text-gray-500" />
        </div>
      </div>
      <div className="p-4">
        <div className="bg-gray-100 rounded-full p-2 mb-4 flex items-center">
          <Search className="text-gray-500 mr-2" size={16} />
          <input
            type="text"
            placeholder="Search posts"
            className="bg-transparent w-full outline-none text-sm"
            value={searchTerm}
            onChange={handleSearch}
          />
        </div>
        <div className="flex mb-4">
          {['hot', 'cold', 'neutral', 'all'].map((category) => (
            <button 
              key={category}
              className={`mr-4 font-semibold transition-colors duration-300 ${
                activeBlogCategory === category 
                  ? category === 'hot' 
                    ? 'text-red-500' 
                    : category === 'cold' 
                      ? 'text-blue-500' 
                      : category === 'neutral'
                        ? 'text-green-500'
                        : 'text-black'
                  : 'text-gray-500'
              }`}
              onClick={() => setActiveBlogCategory(category)}
            >
              {category === 'hot' ? 'Hot Takes 游댠' : category === 'cold' ? 'Cold Takes 仇勇' : category === 'neutral' ? 'Neutral Takes 游릭' : 'All'}
            </button>
          ))}
        </div>
        <div className="space-y-4 transition-all duration-300">
          {filteredBlogPosts.map((post) => (
            <div 
              key={post.id} 
              className="flex items-center p-3 bg-gray-50 rounded-lg transition-all duration-300 hover:bg-gray-100 cursor-pointer"
              onClick={() => handlePostClick(post)}
            >
              <div className="w-12 h-12 bg-gray-300 rounded-full mr-3 flex-shrink-0"></div>
              <div className="flex-grow">
                <h3 className="font-semibold">{post.postName}</h3>
                <p className="text-gray-500 text-sm">{post.date || 'No date'}</p>
              </div>
              <div className="flex items-center">
                <button 
                  onClick={(e) => { e.stopPropagation(); handleVote(post.id, 'hot'); }}
                  className={`mr-2 p-1 rounded ${userVotes[post.id] === 'hot' ? 'bg-red-100' : 'hover:bg-red-100'}`}
                >
                  <ThumbsUp size={16} className={`${userVotes[post.id] === 'hot' ? 'text-red-500' : 'text-gray-500'}`} />
                </button>
                <span className="mr-2">{post.hotVotes || 0}</span>
                <button 
                  onClick={(e) => { e.stopPropagation(); handleVote(post.id, 'cold'); }}
                  className={`mr-2 p-1 rounded ${userVotes[post.id] === 'cold' ? 'bg-blue-100' : 'hover:bg-blue-100'}`}
                >
                  <ThumbsDown size={16} className={`${userVotes[post.id] === 'cold' ? 'text-blue-500' : 'text-gray-500'}`} />
                </button>
                <span className="mr-2">{post.coldVotes || 0}</span>
                <div className="text-xl">
                  {getPostCategory(post) === 'hot' ? '游댠' : getPostCategory(post) === 'cold' ? '仇勇' : '游릭'}
                </div>
              </div>
            </div>
          ))}
        </div>
      </div>
      {selectedPost && (
        <PostModal 
          post={selectedPost} 
          onClose={() => setSelectedPost(null)}
          handleVote={handleVote}
          userVotes={userVotes}
        />
      )}
    </div>
  );
};

export default BlogModal;
