import React from 'react';
import { ChevronLeft, ExternalLink, Tag } from 'lucide-react';
import CustomCursor from './CustomCursor';

const ProjectModal = ({ isZooming, closeProjectModal, activeProject }) => {
  return (
    <div
      className={`fixed top-0 left-0 w-full h-full bg-gray-900 bg-opacity-95 z-50 overflow-auto transition-all duration-500 ease-in-out ${
        isZooming ? 'scale-100 opacity-100' : 'scale-95 opacity-0'
      }`}
    >
      <CustomCursor />
      <div className="max-w-6xl mx-auto p-8">
        <div className="bg-gray-800 rounded-2xl shadow-2xl overflow-hidden">
          <div className="flex items-center justify-between p-6 border-b border-gray-700">
            <div className="flex items-center">
              <button 
                onClick={closeProjectModal} 
                className="mr-4 hover:bg-gray-700 hover:bg-opacity-50 rounded-full p-2 text-purple-400 transition-colors duration-300"
              >
                <ChevronLeft size={24} />
              </button>
              <h2 className="font-bold text-3xl text-white">{activeProject?.title}</h2>
            </div>
            <a 
              href={activeProject?.link} 
              target="_blank" 
              rel="noopener noreferrer" 
              className="flex items-center px-4 py-2 bg-purple-600 hover:bg-purple-700 rounded-full text-white transition-colors duration-300"
            >
              <ExternalLink size={18} className="mr-2" />
              View on GitHub
            </a>
          </div>
          <div className="p-8">
            <div className="grid grid-cols-1 md:grid-cols-2 gap-8">
              <div>
                <img 
                  src={activeProject?.image} 
                  alt={activeProject?.title} 
                  className="w-full h-auto object-cover rounded-xl shadow-lg transition-transform duration-300 hover:scale-105" 
                />
              </div>
              <div className="space-y-6">
                <p className="text-xl leading-relaxed text-gray-300">{activeProject?.description}</p>
                <div>
                  <h3 className="text-2xl font-bold text-purple-300 mb-4">Project Details</h3>
                  <p className="text-lg whitespace-pre-wrap text-gray-300">{activeProject?.details}</p>
                </div>
                {activeProject?.technologies && (
                  <div>
                    <h4 className="text-xl font-semibold text-purple-300 mb-2">Technologies Used</h4>
                    <div className="flex flex-wrap gap-2">
                      {activeProject.technologies.map((tech, index) => (
                        <span key={index} className="px-3 py-1 bg-gray-700 text-purple-300 rounded-full flex items-center">
                          <Tag size={14} className="mr-1" />
                          {tech}
                        </span>
                      ))}
                    </div>
                  </div>
                )}
              </div>
            </div>
          </div>
        </div>
      </div>
    </div>
  );
};

export default ProjectModal;
