import React from 'react';
import { ChevronLeft, ExternalLink } from 'lucide-react';
import CustomCursor from './CustomCursor';

const ProjectModal = ({ isZooming, closeProjectModal, activeProject }) => {
  return (
    <div
      className={`fixed top-0 left-0 w-full h-full bg-gray-900 z-50 overflow-auto transition-all duration-500 ease-in-out ${
        isZooming ? 'scale-100 opacity-100' : 'scale-95 opacity-0'
      }`}
    >
      <CustomCursor />
      <div className={`flex items-center justify-between p-4 border-b border-gray-700 bg-gray-800`}>
        <div className="flex items-center">
          <button onClick={closeProjectModal} className="mr-2 hover:bg-gray-700 hover:bg-opacity-50 rounded-full p-1 text-purple-400">
            <ChevronLeft size={24} />
          </button>
          <h2 className="font-bold text-3xl font-serif text-purple-300">{activeProject?.title}</h2>
        </div>
        <a href={activeProject?.link} target="_blank" rel="noopener noreferrer" className="flex items-center hover:underline text-purple-400">
          <ExternalLink size={20} className="mr-1" />
          View on GitHub
        </a>
      </div>
      <div className="max-w-4xl mx-auto p-8">
        <img src={activeProject?.image} alt={activeProject?.title} className="w-full h-64 object-cover rounded-xl mb-6" />
        <p className="text-xl leading-relaxed font-serif mb-6 text-gray-300">{activeProject?.description}</p>
        <div className="space-y-4">
          <h3 className="text-2xl font-bold text-purple-300">Project Details</h3>
          <p className="text-lg whitespace-pre-wrap text-gray-300">{activeProject?.details}</p>
        </div>
      </div>
    </div>
  );
};

export default ProjectModal;
