import React, { useState, useEffect, useRef } from 'react';
import { Grid, User, FileText, ChevronLeft, Search, Linkedin, Github, Mail, ThumbsUp, ThumbsDown, BookOpen, Briefcase, GraduationCap } from 'lucide-react';

const InstagramProfile = () => {
  const [activeTab, setActiveTab] = useState('projects');
  const [isTransitioning, setIsTransitioning] = useState(false);
  const [highlightStyle, setHighlightStyle] = useState({});
  const [isBlogModalOpen, setIsBlogModalOpen] = useState(false);
  const [isAboutModalOpen, setIsAboutModalOpen] = useState(false);
  const [activeAboutSection, setActiveAboutSection] = useState(null);
  const [isZooming, setIsZooming] = useState(false);
  const [activeBlogCategory, setActiveBlogCategory] = useState('all');
  const [searchTerm, setSearchTerm] = useState('');
  const [blogPosts, setBlogPosts] = useState([
    { id: 1, title: 'Why PC Gaming is Superior', date: '1w ago', content: 'PC gaming offers unparalleled flexibility and power.', hotVotes: 5, coldVotes: 2 },
    { id: 2, title: 'The History of Video Games', date: '3w ago', content: 'Video games have evolved significantly since the 1970s.', hotVotes: 2, coldVotes: 8 },
    { id: 3, title: 'My Controversial Gaming Opinions', date: '1m ago', content: 'Sometimes, linear storytelling is better than open-world.', hotVotes: 10, coldVotes: 10 },
    { id: 4, title: 'Evolution of Game Engines', date: '2m ago', content: 'Game engines have become more accessible to indie developers.', hotVotes: 3, coldVotes: 7 },
    { id: 5, title: 'Why Mobile Gaming is Underrated', date: '3m ago', content: 'Mobile games have come a long way in terms of quality.', hotVotes: 8, coldVotes: 4 },
  ]);
  const [userVotes, setUserVotes] = useState({});
  
  const tabRefs = useRef({});
  const blogModalRef = useRef(null);

  const profileData = {
    username: 'Jeremiah Warinner',
    quote: '"The only legitimate use of a computer is to play games." - Eugene Jarvis',
    linkedin: 'https://www.linkedin.com/in/jeremiah-warinner-8b162821a/',
    github: 'https://github.com/jeremiahwarinner',
    email: 'mailto:warinnerjeremiah@gmail.com',
  };

  const projects = [
    { title: 'Project 1', image: '/api/placeholder/300/300' },
    { title: 'Project 2', image: '/api/placeholder/300/300' },
    { title: 'Project 3', image: '/api/placeholder/300/300' },
  ];

  const aboutMe = [
    { 
    title: 'My Story', 
    icon: BookOpen,
    color: 'bg-purple-100 text-purple-800',
      content: 'As a passionate Computer Science student with a projected graduation in August 2024, I\'m driven by a curiosity for technology and its potential to solve real-world problems. My journey in tech has been marked by a series of challenging projects and valuable experiences, each contributing to my growth as a developer and problem-solver.'
    },
    { 
      title: 'My Experience', 
      icon: Briefcase,
      color: 'bg-blue-100 text-blue-800',
      content: `Full Stack Developer Intern at Luday, Gothenburg SE (July 2024 - Present):
      â€¢ Designed front end components in Figma
      â€¢ Built front-end components in Next.js
      â€¢ Built REST APIs using Python and Flask
      â€¢ Worked in an Agile Scrum environment

      Inventory Manager at Chick-fil-a, Pearland TX (November 2022 - May 2024):
      â€¢ Maintained and utilized Excel sheets to monitor inventory levels
      â€¢ Monitored sales trends to maintain optimal on-hand product levels
      â€¢ Performed repairs on equipment including Henny Penny fryers
      â€¢ Created and executed plans to improve sales, speed, and accuracy of operations

      Hiring Manager at Rolling Dough Ltd, Houston TX (January 2019 - November 2024):
      â€¢ Maintained and utilized Excel sheets to track employee hours and sales metrics
      â€¢ Automated several tasks such as schedule drafting using Python scripts
      â€¢ Performed repairs and maintenance on office and store equipment
      â€¢ Conducted interviews and performed resume analysis for candidate evaluation`
    },
    { 
      title: 'My Education', 
      icon: GraduationCap,
      content: `Bachelor of Science in Computer Science
      University of Houston-Clear Lake, Houston TX
      Expected Graduation: August 2024

      Related Coursework: Software Engineering, Digital Circuits, Computer Architecture, Intro to Telecom and Networks, Assembly Language x86, Advanced Data Structures and Algorithms, Operating Systems, Design of Database Systems, Probability and Statistics, Computer Forensics, Web App Development, Computer Security, Computer Systems Admin, Network Protocols

      Technical Skills:
      â€¢ Programming Languages: Python, Java, Kotlin, SQL, HTML, CSS, JavaScript
      â€¢ Packages: Django, Pandas, Dash, Numpy, React, Next.js
      â€¢ Operating Systems: Linux, Windows
      â€¢ Tools: Github, Tableau, Excel, MySQL`
    }
  ];

  const totalPosts = blogPosts.length;
  const coldTakes = blogPosts.filter(post => post.coldVotes > post.hotVotes).length;
  const hotTakes = blogPosts.filter(post => post.hotVotes > post.coldVotes).length;
  const neutralTakes = blogPosts.filter(post => post.hotVotes === post.coldVotes).length;

  useEffect(() => {
    if (isTransitioning) {
      const timer = setTimeout(() => setIsTransitioning(false), 300);
      return () => clearTimeout(timer);
    }
  }, [isTransitioning]);

  useEffect(() => {
    const updateHighlight = () => {
      const currentTab = tabRefs.current[activeTab];
      if (currentTab) {
        const { offsetLeft, offsetWidth } = currentTab;
        setHighlightStyle({
          left: `${offsetLeft}px`,
          width: `${offsetWidth}px`,
        });
      }
    };

    updateHighlight();
    window.addEventListener('resize', updateHighlight);
    return () => window.removeEventListener('resize', updateHighlight);
  }, [activeTab]);

  const handleTabChange = (tab) => {
    setIsTransitioning(true);
    setActiveTab(tab);
    if (tab === 'blog') {
      openBlogModal();
    } else {
      setTimeout(() => setIsTransitioning(false), 150);
    }
  };

  const openBlogModal = () => {
    setIsBlogModalOpen(true);
    setTimeout(() => {
      setIsZooming(true);
    }, 50);
  };

  const closeBlogModal = () => {
    setIsZooming(false);
    setTimeout(() => {
      setIsBlogModalOpen(false);
      setActiveTab('projects');
      setIsTransitioning(false);
    }, 300);
  };

  const openAboutModal = (section) => {
    setActiveAboutSection(section);
    setIsAboutModalOpen(true);
    setTimeout(() => {
      setIsZooming(true);
    }, 50);
  };

  const closeAboutModal = () => {
    setIsZooming(false);
    setTimeout(() => {
      setIsAboutModalOpen(false);
      setActiveAboutSection(null);
    }, 300);
  };

  const handleSearch = (event) => {
    setSearchTerm(event.target.value);
    setActiveBlogCategory('all');
  };

  const handleVote = (postId, voteType) => {
    setUserVotes(prevVotes => {
      const currentVote = prevVotes[postId];
      if (currentVote === voteType) {
        const { [postId]: _, ...rest } = prevVotes;
        return rest;
      } else {
        return { ...prevVotes, [postId]: voteType };
      }
    });

    setBlogPosts(prevPosts => 
      prevPosts.map(post => {
        if (post.id === postId) {
          const currentVote = userVotes[postId];
          let newHotVotes = post.hotVotes;
          let newColdVotes = post.coldVotes;

          if (currentVote === 'hot' && voteType === 'cold') {
            newHotVotes--;
            newColdVotes++;
          } else if (currentVote === 'cold' && voteType === 'hot') {
            newHotVotes++;
            newColdVotes--;
          } else if (!currentVote) {
            if (voteType === 'hot') newHotVotes++;
            if (voteType === 'cold') newColdVotes++;
          } else if (currentVote === voteType) {
            if (voteType === 'hot') newHotVotes--;
            if (voteType === 'cold') newColdVotes--;
          }

          return { ...post, hotVotes: newHotVotes, coldVotes: newColdVotes };
        }
        return post;
      })
    );
  };

  const getPostCategory = (post) => {
    if (post.hotVotes > post.coldVotes) return 'hot';
    if (post.coldVotes > post.hotVotes) return 'cold';
    return 'neutral';
  };

  const filteredBlogPosts = blogPosts.filter(post => 
    (activeBlogCategory === 'all' || getPostCategory(post) === activeBlogCategory) &&
    (post.title.toLowerCase().includes(searchTerm.toLowerCase()) ||
     post.content.toLowerCase().includes(searchTerm.toLowerCase()))
  );

  const renderGrid = () => {
    switch (activeTab) {
      case 'projects':
        return projects.map((project, index) => (
          <div key={index} className="aspect-square bg-gray-200 flex items-center justify-center">
            <img src={project.image} alt={project.title} className="w-full h-full object-cover" />
          </div>
        ));
      case 'about':
        return aboutMe.map((item, index) => (
          <div key={index} onClick={() => openAboutModal(item)} className="aspect-square bg-white border border-gray-200 p-4 flex flex-col items-center justify-center cursor-pointer hover:bg-gray-50 transition-colors duration-200">
            <item.icon size={48} className="mb-4" />
            <h3 className="font-bold text-lg text-center">{item.title}</h3>
          </div>
        ));
      default:
        return null;
    }
  };

  const renderAboutModal = () => (
    <div 
      ref={blogModalRef}
      className={`fixed top-0 left-0 w-full h-full bg-white z-50 overflow-auto transition-all duration-500 ease-in-out ${
        isZooming ? 'scale-100 opacity-100' : 'scale-95 opacity-0'
      }`}
    >
      <div className="flex items-center justify-between p-4 border-b border-gray-200">
        <div className="flex items-center">
          <button onClick={closeAboutModal} className="mr-2">
            <ChevronLeft />
          </button>
          <h2 className="font-semibold text-2xl">{activeAboutSection?.title}</h2>
        </div>
      </div>
      <div className="max-w-2xl mx-auto p-8">
        {activeAboutSection?.title === 'My Story' && (
          <div className="space-y-6">
            <p className="text-lg leading-relaxed">
              As a passionate Computer Science student with a projected graduation in August 2024, I'm driven by a curiosity for technology and its potential to solve real-world problems. My journey in tech has been marked by a series of challenging projects and valuable experiences, each contributing to my growth as a developer and problem-solver.
            </p>
            <p className="text-lg leading-relaxed">
              From crafting intricate algorithms to building user-friendly interfaces, I've embraced every opportunity to expand my skills and knowledge. My academic journey has been complemented by hands-on projects and internships, allowing me to apply classroom theories to real-world scenarios.
            </p>
            <p className="text-lg leading-relaxed">
              As I approach the culmination of my formal education, I'm excited about the possibilities that lie ahead. I'm eager to contribute my skills to innovative projects and continue learning in this ever-evolving field.
            </p>
          </div>
        )}
        {activeAboutSection?.title === 'My Experience' && (
          <div className="space-y-8">
            <div>
              <h3 className="text-xl font-semibold mb-2">Full Stack Developer Intern</h3>
              <p className="text-gray-600 mb-2">Luday, Gothenburg SE (July 2024 - Present)</p>
              <ul className="list-disc pl-5 space-y-1">
                <li>Designed front end components in Figma</li>
                <li>Built front-end components in Next.js</li>
                <li>Built REST APIs using Python and Flask</li>
                <li>Worked in an Agile Scrum environment</li>
              </ul>
            </div>
            <div>
              <h3 className="text-xl font-semibold mb-2">Inventory Manager</h3>
              <p className="text-gray-600 mb-2">Chick-fil-a, Pearland TX (November 2022 - May 2024)</p>
              <ul className="list-disc pl-5 space-y-1">
                <li>Maintained and utilized Excel sheets to monitor inventory levels</li>
                <li>Monitored sales trends to maintain optimal on-hand product levels</li>
                <li>Performed repairs on equipment including Henny Penny fryers</li>
                <li>Created and executed plans to improve sales, speed, and accuracy of operations</li>
              </ul>
            </div>
            <div>
              <h3 className="text-xl font-semibold mb-2">Hiring Manager</h3>
              <p className="text-gray-600 mb-2">Rolling Dough Ltd, Houston TX (January 2019 - November 2024)</p>
              <ul className="list-disc pl-5 space-y-1">
                <li>Maintained and utilized Excel sheets to track employee hours and sales metrics</li>
                <li>Automated several tasks such as schedule drafting using Python scripts</li>
                <li>Performed repairs and maintenance on office and store equipment</li>
                <li>Conducted interviews and performed resume analysis for candidate evaluation</li>
              </ul>
            </div>
          </div>
        )}
        {activeAboutSection?.title === 'My Education' && (
          <div className="space-y-6">
            <div>
              <h3 className="text-xl font-semibold mb-2">Bachelor of Science in Computer Science</h3>
              <p className="text-gray-600">University of Houston-Clear Lake, Houston TX</p>
              <p className="text-gray-600">Expected Graduation: August 2024</p>
            </div>
            <div>
              <h4 className="text-lg font-semibold mb-2">Related Coursework:</h4>
              <p className="text-gray-700 leading-relaxed">
                Software Engineering, Digital Circuits, Computer Architecture, Intro to Telecom and Networks, Assembly Language x86, Advanced Data Structures and Algorithms, Operating Systems, Design of Database Systems, Probability and Statistics, Computer Forensics, Web App Development, Computer Security, Computer Systems Admin, Network Protocols
              </p>
            </div>
            <div>
              <h4 className="text-lg font-semibold mb-2">Technical Skills:</h4>
              <ul className="list-disc pl-5 space-y-1">
                <li><span className="font-semibold">Programming Languages:</span> Python, Java, Kotlin, SQL, HTML, CSS, JavaScript</li>
                <li><span className="font-semibold">Packages:</span> Django, Pandas, Dash, Numpy, React, Next.js</li>
                <li><span className="font-semibold">Operating Systems:</span> Linux, Windows</li>
                <li><span className="font-semibold">Tools:</span> Github, Tableau, Excel, MySQL</li>
              </ul>
            </div>
          </div>
        )}
      </div>
    </div>
  );

  const renderBlogModal = () => (
    <div 
      ref={blogModalRef}
      className={`fixed top-0 left-0 w-full h-full bg-white z-50 overflow-auto transition-all duration-500 ease-in-out ${
        isZooming ? 'scale-100 opacity-100' : 'scale-95 opacity-0'
      }`}
    >
      <div className="flex items-center justify-between p-4 border-b border-gray-200">
        <div className="flex items-center">
          <button onClick={closeBlogModal} className="mr-2">
            <ChevronLeft />
          </button>
          <h2 className="font-semibold">Blog Posts</h2>
        </div>
        <Search className="text-gray-500" />
      </div>
      <div className="p-4">
        <div className="bg-gray-100 rounded-full p-2 mb-4 flex items-center">
          <Search className="text-gray-500 mr-2" size={16} />
          <input
            type="text"
            placeholder="Search posts"
            className="bg-transparent w-full outline-none text-sm"
            value={searchTerm}
            onChange={handleSearch}
          />
        </div>
        <div className="flex mb-4">
          {['hot', 'cold', 'neutral', 'all'].map((category) => (
            <button 
              key={category}
              className={`mr-4 font-semibold transition-colors duration-300 ${
                activeBlogCategory === category 
                  ? category === 'hot' 
                    ? 'text-red-500' 
                    : category === 'cold' 
                      ? 'text-blue-500' 
                      : category === 'neutral'
                        ? 'text-green-500'
                        : 'text-black'
                  : 'text-gray-500'
              }`}
              onClick={() => setActiveBlogCategory(category)}
            >
              {category === 'hot' ? 'Hot Takes ðŸ”¥' : category === 'cold' ? 'Cold Takes â„ï¸' : category === 'neutral' ? 'Neutral Takes ðŸŸ¢' : 'All'}
            </button>
          ))}
        </div>
        <div className="space-y-4 transition-all duration-300">
          {filteredBlogPosts.map((post) => (
            <div key={post.id} className="flex items-center p-3 bg-gray-50 rounded-lg transition-all duration-300 hover:bg-gray-100">
              <div className="w-12 h-12 bg-gray-300 rounded-full mr-3 flex-shrink-0"></div>
              <div className="flex-grow">
                <h3 className="font-semibold">{post.title}</h3>
                <p className="text-gray-500 text-sm">{post.date}</p>
              </div>
              <div className="flex items-center">
                <button 
                  onClick={() => handleVote(post.id, 'hot')} 
                  className={`mr-2 p-1 rounded ${
                    userVotes[post.id] === 'hot' 
                      ? 'bg-red-100' 
                      : 'hover:bg-red-100'
                  }`}
                >
                  <ThumbsUp size={16} className={`${userVotes[post.id] === 'hot' ? 'text-red-500' : 'text-gray-500'}`} />
                </button>
                <span className="mr-2">{post.hotVotes}</span>
                <button 
                  onClick={() => handleVote(post.id, 'cold')} 
                  className={`mr-2 p-1 rounded ${
                    userVotes[post.id] === 'cold' 
                      ? 'bg-blue-100' 
                      : 'hover:bg-blue-100'
                  }`}
                >
                  <ThumbsDown size={16} className={`${userVotes[post.id] === 'cold' ? 'text-blue-500' : 'text-gray-500'}`} />
                </button>
                <span className="mr-2">{post.coldVotes}</span>
                <div className="text-xl">
                  {getPostCategory(post) === 'hot' ? 'ðŸ”¥' : getPostCategory(post) === 'cold' ? 'â„ï¸' : 'ðŸŸ¢'}
                </div>
              </div>
            </div>
          ))}
        </div>
      </div>
    </div>
  );

  const tabs = [
    { name: 'PROJECTS', icon: Grid, value: 'projects' },
    { name: 'ABOUT ME', icon: User, value: 'about' },
    { name: 'BLOG', icon: FileText, value: 'blog' },
  ];

  return (
    <div className="max-w-3xl mx-auto p-4">
      <header className="flex items-center mb-8">
        <div className="w-20 h-20 rounded-full bg-gray-300 mr-8"></div>
        <div>
          <h1 className="text-2xl font-bold mb-2">{profileData.username}</h1>
          <div className="flex space-x-4 mb-2">
            <span><strong>{totalPosts}</strong> posts</span>
            <span><strong>{coldTakes}</strong> â„ï¸</span>
            <span><strong>{hotTakes}</strong> ðŸ”¥</span>
            <span><strong>{neutralTakes}</strong> ðŸŸ¢</span>
          </div>
          <div className="font-medium text-sm italic mb-2">{profileData.quote}</div>
          <div className="flex space-x-4">
            <a href={profileData.linkedin} target="_blank" rel="noopener noreferrer" className="text-blue-500 hover:text-blue-700">
              <Linkedin size={20} />
            </a>
            <a href={profileData.github} target="_blank" rel="noopener noreferrer" className="text-gray-700 hover:text-black">
              <Github size={20} />
            </a>
            <a href={profileData.email} className="text-red-500 hover:text-red-700">
              <Mail size={20} />
            </a>
          </div>
        </div>
      </header>

      <nav className="border-t border-gray-300 mb-8 relative">
        <div 
          className="absolute top-0 h-0.5 bg-black transition-all duration-300 ease-in-out"
          style={highlightStyle}
        />
        <ul className="flex justify-center space-x-12 -mt-px">
          {tabs.map((tab) => (
            <li 
              key={tab.value} 
              ref={el => tabRefs.current[tab.value] = el}
              className="pt-3"
            >
              <button 
                onClick={() => handleTabChange(tab.value)}
                className={`flex items-center transition-colors duration-300 ${activeTab === tab.value ? 'text-black' : 'text-gray-500'}`}
              >
                <tab.icon size={12} className="mr-1" />
                {tab.name}
              </button>
            </li>
          ))}
        </ul>
      </nav>

      <div className={`grid grid-cols-3 gap-4 transition-opacity duration-300 ${isTransitioning ? 'opacity-0' : 'opacity-100'}`}>
        {renderGrid()}
      </div>

      {isBlogModalOpen && renderBlogModal()}
      {isAboutModalOpen && renderAboutModal()}
    </div>
  );
};

export default InstagramProfile;
