import React from 'react';
import { ChevronLeft } from 'lucide-react';

const AboutModal = ({ isZooming, closeAboutModal, activeAboutSection }) => {
  return (
    <div
      className={`fixed top-0 left-0 w-full h-full bg-gray-900 z-50 overflow-auto transition-all duration-500 ease-in-out ${
        isZooming ? 'scale-100 opacity-100' : 'scale-95 opacity-0'
      }`}
    >
      <div className={`flex items-center justify-between p-4 border-b border-gray-700 bg-gray-800`}>
        <div className="flex items-center">
          <button onClick={closeAboutModal} className="mr-2 hover:bg-gray-700 hover:bg-opacity-50 rounded-full p-1 text-purple-400">
            <ChevronLeft size={24} />
          </button>
          <h2 className="font-bold text-3xl font-serif text-purple-300">{activeAboutSection?.title}</h2>
        </div>
      </div>
      <div className="max-w-4xl mx-auto p-8">
        <pre className="whitespace-pre-wrap font-sans text-lg leading-relaxed text-gray-300">{activeAboutSection?.content}</pre>
      </div>
    </div>
  );
};

export default AboutModal;
