import React, { useState, useEffect, useRef } from 'react';
import { Grid, User, FileText } from 'lucide-react';
import ProfileHeader from './ProfileHeader';
import TabNavigation from './TabNavigation';
import ProjectGrid from './ProjectGrid';
import AboutGrid from './AboutGrid';
import BlogModal from './BlogModal';
import AboutModal from './AboutModal';
import ProjectModal from './ProjectModal';
import CreateBlogPost from './CreateBlogPost';
import { profileData, projects, aboutMe } from './data';

const InstagramProfile = () => {
  const [activeTab, setActiveTab] = useState('projects');
  const [isTransitioning, setIsTransitioning] = useState(false);
  const [isBlogModalOpen, setIsBlogModalOpen] = useState(false);
  const [isAboutModalOpen, setIsAboutModalOpen] = useState(false);
  const [activeAboutSection, setActiveAboutSection] = useState(null);
  const [isProjectModalOpen, setIsProjectModalOpen] = useState(false);
  const [activeProject, setActiveProject] = useState(null);
  const [isZooming, setIsZooming] = useState(false);
  const [blogPosts, setBlogPosts] = useState([]);
  const [isCreatePostModalOpen, setIsCreatePostModalOpen] = useState(false);
  const [userVotes, setUserVotes] = useState({});

  const tabRefs = useRef({});

  useEffect(() => {
    fetchBlogPosts();
  }, []);

  useEffect(() => {
    if (isTransitioning) {
      const timer = setTimeout(() => setIsTransitioning(false), 300);
      return () => clearTimeout(timer);
    }
  }, [isTransitioning]);

  const fetchBlogPosts = async () => {
    try {
      const response = await fetch('http://localhost:5000/get_posts');
      if (!response.ok) {
        throw new Error(`HTTP error! status: ${response.status}`);
      }
      const data = await response.json();
      console.log('Fetched data:', data);
      
      if (Array.isArray(data.posts)) {
        const postsWithVotes = data.posts.map(post => ({
          ...post,
          hot_like_count: post.hot_like_count || 0,
          cold_like_count: post.cold_like_count || 0
        }));
        setBlogPosts(postsWithVotes);
      } else if (typeof data === 'object' && data !== null) {
        const postsArray = Object.values(data).map(post => ({
          ...post,
          hot_like_count: post.hot_like_count || 0,
          cold_like_count: post.cold_like_count || 0
        }));
        setBlogPosts(postsArray);
      } else {
        console.error('Unexpected data structure:', data);
        setBlogPosts([]);
      }
    } catch (error) {
      console.error('Error fetching blog posts:', error);
      setBlogPosts([]);
    }
  };

  const handleTabChange = (tab) => {
    setIsTransitioning(true);
    setActiveTab(tab);
    if (tab === 'blog') {
      openBlogModal();
    } else {
      setTimeout(() => setIsTransitioning(false), 150);
    }
  };

  const openBlogModal = () => {
    setIsBlogModalOpen(true);
    setTimeout(() => {
      setIsZooming(true);
    }, 50);
  };

  const closeBlogModal = () => {
    setIsZooming(false);
    setTimeout(() => {
      setIsBlogModalOpen(false);
      setActiveTab('projects');
      setIsTransitioning(false);
    }, 300);
  };

  const openAboutModal = (section) => {
    setActiveAboutSection(section);
    setIsAboutModalOpen(true);
    setTimeout(() => {
      setIsZooming(true);
    }, 50);
  };

  const closeAboutModal = () => {
    setIsZooming(false);
    setTimeout(() => {
      setIsAboutModalOpen(false);
      setActiveAboutSection(null);
    }, 300);
  };

  const openProjectModal = (project) => {
    setActiveProject(project);
    setIsProjectModalOpen(true);
    setTimeout(() => {
      setIsZooming(true);
    }, 50);
  };

  const closeProjectModal = () => {
    setIsZooming(false);
    setTimeout(() => {
      setIsProjectModalOpen(false);
      setActiveProject(null);
    }, 300);
  };

  const handleVote = async (postId, voteType) => {
    try {
      const post = blogPosts.find(p => p.id === postId);
      if (!post) {
        console.error('Post not found');
        return;
      }

      let newHotLikeCount = post.hot_like_count || 0;
      let newColdLikeCount = post.cold_like_count || 0;

      if (userVotes[postId] === voteType) {
        // User is un-voting
        if (voteType === 'hot') {
          newHotLikeCount -= 1;
        } else {
          newColdLikeCount -= 1;
        }
      } else {
        // User is voting or changing vote
        if (userVotes[postId]) {
          // Changing vote
          if (voteType === 'hot') {
            newHotLikeCount += 1;
            newColdLikeCount -= 1;
          } else {
            newHotLikeCount -= 1;
            newColdLikeCount += 1;
          }
        } else {
          // New vote
          if (voteType === 'hot') {
            newHotLikeCount += 1;
          } else {
            newColdLikeCount += 1;
          }
        }
      }

      const response = await fetch(`http://localhost:5000/update_posts/${postId}`, {
        method: 'PATCH',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify({
          hotLikeCount: newHotLikeCount,
          coldLikeCount: newColdLikeCount,
        }),
      });

      if (!response.ok) {
        throw new Error(`HTTP error! status: ${response.status}`);
      }

      // Update local state
      setBlogPosts(prevPosts => 
        prevPosts.map(p => 
          p.id === postId ? { ...p, hot_like_count: newHotLikeCount, cold_like_count: newColdLikeCount } : p
        )
      );

      setUserVotes(prevVotes => {
        if (prevVotes[postId] === voteType) {
          const { [postId]: _, ...rest } = prevVotes;
          return rest;
        } else {
          return { ...prevVotes, [postId]: voteType };
        }
      });

      console.log(`Updated post ${postId}: Hot votes - ${newHotLikeCount}, Cold votes - ${newColdLikeCount}`);
      
      // Fetch updated posts after successful vote
      await fetchBlogPosts();
    } catch (error) {
      console.error('Error updating vote:', error);
    }
  };

  const handlePostCreated = (newPost) => {
    setBlogPosts(prevPosts => [newPost, ...prevPosts]);
    setIsCreatePostModalOpen(false);
  };

  const tabs = [
    { name: 'PROJECTS', icon: Grid, value: 'projects' },
    { name: 'ABOUT ME', icon: User, value: 'about' },
    { name: 'BLOG', icon: FileText, value: 'blog' },
  ];

  return (
    <div className="max-w-3xl mx-auto p-4">
      <ProfileHeader profileData={profileData} blogPosts={blogPosts} />

      <TabNavigation 
        tabs={tabs} 
        activeTab={activeTab} 
        handleTabChange={handleTabChange} 
        tabRefs={tabRefs} 
      />

      <div className={`transition-opacity duration-300 ${isTransitioning ? 'opacity-0' : 'opacity-100'}`}>
        {activeTab === 'projects' && (
          <div className="grid grid-cols-3 gap-4">
            <ProjectGrid projects={projects} openProjectModal={openProjectModal} />
          </div>
        )}
        {activeTab === 'about' && <AboutGrid aboutMe={aboutMe} openAboutModal={openAboutModal} />}
      </div>

      {isBlogModalOpen && (
        <BlogModal 
          isZooming={isZooming}
          closeBlogModal={closeBlogModal}
          blogPosts={blogPosts}
          userVotes={userVotes}
          handleVote={handleVote}
          setIsCreatePostModalOpen={setIsCreatePostModalOpen}
        />
      )}
      {isAboutModalOpen && (
        <AboutModal 
          isZooming={isZooming}
          closeAboutModal={closeAboutModal}
          activeAboutSection={activeAboutSection}
        />
      )}
      {isProjectModalOpen && (
        <ProjectModal 
          isZooming={isZooming}
          closeProjectModal={closeProjectModal}
          activeProject={activeProject}
        />
      )}
      {isCreatePostModalOpen && (
        <CreateBlogPost
          onClose={() => setIsCreatePostModalOpen(false)}
          onPostCreated={handlePostCreated}
        />
      )}
    </div>
  );
};

export default InstagramProfile;
