import React from 'react';
import { X, ThumbsUp, ThumbsDown } from 'lucide-react';

const PostModal = ({ post, onClose, handleVote, userVotes }) => {
  return (
    <div className="fixed inset-0 bg-black bg-opacity-50 flex items-center justify-center z-60">
      <div className="bg-white rounded-lg w-full max-w-2xl max-h-[80vh] overflow-auto">
        <div className="flex justify-between items-center p-4 border-b border-gray-200">
          <h2 className="font-semibold text-xl">{post.postName}</h2>
          <button onClick={onClose} className="text-gray-500 hover:text-gray-700">
            <X size={24} />
          </button>
        </div>
        <div className="p-4">
          <p className="text-gray-700 mb-4">{post.postContent}</p>
          <div className="flex items-center justify-end">
            <button 
              onClick={() => handleVote(post.id, 'hot')}
              className={`mr-2 p-1 rounded ${userVotes[post.id] === 'hot' ? 'bg-red-100' : 'hover:bg-red-100'}`}
            >
              <ThumbsUp size={16} className={`${userVotes[post.id] === 'hot' ? 'text-red-500' : 'text-gray-500'}`} />
            </button>
            <span className="mr-2">{post.hotVotes || 0}</span>
            <button 
              onClick={() => handleVote(post.id, 'cold')}
              className={`mr-2 p-1 rounded ${userVotes[post.id] === 'cold' ? 'bg-blue-100' : 'hover:bg-blue-100'}`}
            >
              <ThumbsDown size={16} className={`${userVotes[post.id] === 'cold' ? 'text-blue-500' : 'text-gray-500'}`} />
            </button>
            <span>{post.coldVotes || 0}</span>
          </div>
        </div>
      </div>
    </div>
  );
};

export default PostModal;
